export const VisibilityControl = ({ showCompleted, setShowCompleted }) => {
  const handleDelete = () => {alert('cleaning')};

  return (
    <div>
      <input
        type="checkbox"
        onChange={(e) => {
          setShowCompleted(!showCompleted);
        }}
      />
      <label>Show task done</label>

      <button onClick={handleDelete}>Clear</button>
    </div>
  );
};
