export const TaskTable = ({ tasks }) => {
  return (
    <table>
      <thead>
        <tr>Tasks</tr>
      </thead>
      <tbody>
        {tasks.map((task) => {
          return (
            <tr key={task.name}>
              <td>
                {task.name} <input type="checkbox" checked={task.done} />
              </td>
            </tr>
          );
        })}
      </tbody>
    </table>
  );
};
