import { TaskRow } from "./TaskRow";

export const TaskTable = ({ tasks, toogleTask }) => {
  const taskTableRows = (doneValue) => {
    return tasks
        .filter(task => task.done === true)
        .map((task) => {
        return (
          <TaskRow task={task} key={task.name} toogleTask={toogleTask} />
        );
      })
  };
  return (
    <table>
      <thead>
        <tr>Tasks</tr>
      </thead>
      <tbody>
        {taskTableRows(false)}
      </tbody>
    </table>
  );
};
