import { useState } from "react";
import "./App.css";


function App() {
  const [newTaskName, setNewTaskName] = useState();

  return (
    <div className="App">
      <input
        type="text"
        placeholder="Enter a new task"
        onChange={(e) => setNewTaskName(e.target.value)}
      />
      <button onClick={() => alert( newTaskName)}>Save task</button>
    </div>
  );
}

export default App;
