import { useState } from "react";
import "./App.css";

import TaskCreator from "./components/TaskCreator";

function App() {
  const [taskItems, setTaskItem] = useState([
    { name: "My first task", done: false },
    { name: "My second task", done: false },
    { name: "My third task", done: false },
  ]);

  function createTask(taskName) {
    const result = taskItems.find(task => task.name === taskName);
    if(result) return alert('wrong');

    setTaskItem([...taskItems, {name: taskName, done: false}])
  }

  return (
    <div className="App">
      <TaskCreator createTask={createTask} number={30}/>

      {
        <table>
          <thead>
            <tr>Tasks</tr>
          </thead>
          <tbody>
            {taskItems.map((task) => {
              return (
                <tr key={task.name}>
                  <td>{task.name}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      }
    </div>
  );
}

export default App;
