import { useState } from "react";
import "./App.css";

import TaskCreator from "./components/TaskCreator";


function App() {

  const [taskItems, setTaskItem]  = useState([
    {name: "My first task", done: false},
    {name: "My second task", done: false},
    {name: "My third task", done: false}
  ]);

  return (
    <div className="App">
        <TaskCreator/>

        {
          taskItems.map(task => {
            <div>
              {task.name}
            </div>
          })
        }
    </div>
  );
}

export default App;
