export const VisibilityControl = ({  setShowCompleted, cleanTask }) => {

  return (
    <div>
      <input
        type="checkbox"
        onChange={(e) => {
          setShowCompleted(e.target.checked);
        }}
      />
      <label>Show task done</label>

      <button onClick={cleanTask}>Clear</button>
    </div>
  );
};
